class Solution {

    public int[] largeNum(int[] nums,int k){
        int n = nums.length;
        Stack<Integer> stack = new Stack<>();
        for(int i = 0;i < n;i++){

            while(stack.size() > 0 && stack.peek() < nums[i] && k - stack.size() <= n - i - 1){
                stack.pop();
            }

            stack.push(nums[i]);
        }

        int[] ans = new int[stack.size()];
        int ptr = stack.size() - 1;

        while(!stack.isEmpty()){
            ans[ptr--] = stack.pop();
        }
        return ans;
    }

    boolean isGreater(int[] max1,int[] max2,int i,int j){

        while(i < max1.length || j < max2.length){

            if(i >= max1.length) return false;
            else if(j >= max2.length) return true;
            else if(max1[i] < max2[j]) return false;
            else if(max1[i] > max2[j]) return true;
            else{
                i++;
                j++;
            }
        }

        return true;

    }



