public class Solution {
    public int[] MaxNumber(int[] nums1, int[] nums2, int k) {
        int m = nums1.Length;
        int n = nums2.Length;
        int[] result = new int[k];
        for (int i = Math.Max(0, k - n); i <= k && i <= m; i++)
        {
            int[] candidate = MergeArrays(GetMaxSubsequence(nums1, i), GetMaxSubsequence(nums2, k - i));
            if (IsGreater(candidate, 0, result, 0)) result = candidate;
        }
        return result;
    }

    private int[] GetMaxSubsequence(int[] nums, int k)
    {
        int[] result = new int[k];
        int length = 0;

        for (int i = 0; i < nums.Length; i++)
        {
            while (length > 0 && nums[i] > result[length - 1] && nums.Length - i + length > k) length--;
            if (length < k) result[length++] = nums[i];
        }

        return result;
    }

    private int[] MergeArrays(int[] nums1, int[] nums2)
    {
        int m = nums1.Length;
        int n = nums2.Length;
        int[] merged = new int[m + n];
        int i = 0, j = 0, idx = 0;

        while (i < m && j < n)
        {
            if (IsGreater(nums1, i, nums2, j)) merged[idx++] = nums1[i++];
            else merged[idx++] = nums2[j++]; 
        }   
        while (i < m) merged[idx++] = nums1[i++];
        while (j < n) merged[idx++] = nums2[j++];

        return merged;
    }

    private bool IsGreater(int[] nums1, int idx1, int[] nums2, int idx2)
    {
        int m = nums1.Length;
        int n = nums2.Length;

        while (idx1 < m && idx2 < n)
        {
            if (nums1[idx1] > nums2[idx2]) return true;
            else if (nums1[idx1] < nums2[idx2]) return false;

            idx1++;
            idx2++;
        }

        return idx1 != m;
    }
}
