class Solution:
    def maxNumber(self, nums1: List[int], nums2: List[int], k: int) -> List[int]:
        m,n=len(nums1),len(nums2)
        def nge(arr,par):
            x=m if par==1 else n
            right=[-1]*x
            stack,temp=[],nums1 if par==1 else nums2
            for i in range(x-1,-1,-1):
                while stack and temp[stack[-1]]<=temp[i]: stack.pop()
                if stack: right[i]=stack[-1]
                stack.append(i)
            return right
        right1,right2=nge(nums1,1),nge(nums2,2)
        def mmax1(x):
            res=[]
            for i in range(m):
                if x==0: break
                r1=right1[i]
                if r1==-1 or m-r1<x:
                    res.append(nums1[i])
                    x-=1
            return res
        def mmax2(x):
            res=[]
            for i in range(n):
                if x==0: break
                r2=right2[i]
                if r2==-1 or n-r2<x:
                    res.append(nums2[i])
                    x-=1
            return res
        def isgreater(gg1, gg2, i, j):
            while i < len(gg1) and j < len(gg2) and gg1[i] == gg2[j]:
                i += 1
                j += 1
            if(j == len(gg2)): return 1
            if (i < len(gg1) and gg1[i] > gg2[j]): return 1
            return 0
        def solve(gg1,gg2):
            l1,l2=len(gg1),len(gg2)
            i,j=0,0
            res=[]
            while i<l1 and j<l2:
                if isgreater(gg1, gg2, i, j):
                    res.append(gg1[i])
                    i+=1
                else:
                    res.append(gg2[j])
                    j+=1
            while i<l1:
                res.append(gg1[i])
                i+=1
            while j<l2:
                res.append(gg2[j])
                j+=1
            return res
        ans=[]
        for i in range(k+1):
            j=k-i
            if i<=m and j<=n:
                res=solve(mmax1(i),mmax2(j))
                if not ans or res>ans:
                    ans=res
        return ans






