class Solution {
    vector<int> removek(vector<int> &nums, int k) {
        vector<int> res;
        for (auto num : nums) {
            while (res.size() && res.back() < num && k) {
                res.pop_back();
                k--;
            }
            res.push_back(num);
        }
        while(k) {
            res.pop_back();
            k--;
        }
        return res;
    }

    vector<int> merge(vector<int> &arr1, vector<int> &arr2) {
        vector<int> res;
        int i = 0, j = 0;
        int N1 = arr1.size(), N2 = arr2.size();
        while(i < N1 && j < N2) {
            vector<int> tmp1(arr1.begin() + i, arr1.end());
            vector<int> tmp2(arr2.begin() + j, arr2.end());
            if (tmp1 > tmp2) {
                res.push_back(arr1[i++]);
            } else {
                res.push_back(arr2[j++]);
            }
        }
        while(i < N1) {
            res.push_back(arr1[i++]);
        }
        while(j < N2) {
            res.push_back(arr2[j++]);
        }
        return res;
    }


public:
    vector<int> maxNumber(vector<int>& nums1, vector<int>& nums2, int k) {
        int N1 = nums1.size(), N2 = nums2.size();
        vector<int> res;

        // N1 - i >= 0 && N2 - (k - i) >= 0
        for (int i = max(0, k - N2); i <= k && i <= N1; i++) {
            auto arr1 = removek(nums1, N1 - i);
            auto arr2 = removek(nums2, N2 - (k - i));
            auto arr = merge(arr1, arr2);
            res = max(res, arr);
        }
        return res;
    }
};
