vector<int> removeDigits(vector<int> &nums, int k) {
	if(k == 0) return nums;
	if(k == nums.size()) return {};
	stack<int> st;
	st.push(nums[0]);
	for(int i = 1; i < nums.size(); i++) {
		while(!st.empty() && st.top() < nums[i] && k > 0) {
			st.pop();
			k--;
		}
		st.push(nums[i]);
	}
	while(k > 0) {
		st.pop();
		k--;
	}
	vector<int> ans;
	while(!st.empty()) {
		ans.push_back(st.top());
		st.pop();
	}
	reverse(ans.begin(), ans.end());
	return ans;
}

vector<int> merge(vector<int> &v1, vector<int> &v2) {
	if(v1.size() == 0) return v2;
	if(v2.size() == 0) return v1;
	vector<int> v;
	int i = 0, j = 0;
	while(i < v1.size() && j < v2.size()) {
		if(v1[i] == v2[j]) {
			int a, b;
			int i1 = i, j1 = j;
			while(i1 < v1.size() && j1 < v2.size() && v1[i1] == v2[j1]) {
				i1++;
				j1++;
			}
			if(i1 < v1.size()) a = v1[i1];
			else a = INT_MIN;
			if(j1 < v2.size()) b = v2[j1];
			else b = INT_MIN;
			if(a >= b) {
				v.push_back(v1[i]);
				i++;
			}
			else {
				v.push_back(v2[j]);
				j++;
			}
		}
		else if(v1[i] > v2[j]) {
			v.push_back(v1[i]);
			i++;
		}
		else {
			v.push_back(v2[j]);
			j++;
		}
	}
	while(i < v1.size()) {
		v.push_back(v1[i]);
		i++;
	}
	while(j < v2.size()) {
		v.push_back(v2[j]);
		j++;
	}
	return v;
