    vector<int>HighestNum(vector<int>nums,int k){
        int n=nums.size();
        vector<int>ans(k);
        for(int i=0,j=0;i<n;++i){
            while(n -i + j > k && j > 0 && ans[j-1] < nums[i]){
                j--; 
            }
            if(j<k){
                ans[j++]=nums[i];
            }
        }
        return ans;
    }
vector<int> merge(vector<int> &nums1, vector<int> &nums2, int k)
