        for(int i=0;i<nums.size();i++){
            if(s.size() + (nums.size()-i) < k) break;
            while(s.size() && s.size() + (nums.size()-i) > k && s.back() < nums[i]) s.pop_back();
            if(s.size() < k) s.push_back(nums[i]);
        }

        return s.size() >= k;
    }

