vector<int> findMaxSubsequnce(const vector<int>& v, int k) {
        vector<int> ans;
        for (int i = 0, n = size(v); i < n; ++i) {
            const int left = n - i;
            while (!empty(ans) && size(ans) + left > k && v[i] > ans.back()) {
                ans.pop_back();
            }
            if (size(ans) < k) {
                ans.push_back(v[i]);
            }
        }
        return ans;
    }

    vector<int> merge(const vector<int>& v1, const vector<int>& v2) {
        const int n = size(v1);
        const int m = size(v2);
        int i = 0;
        int j = 0;
        vector<int> ans;
        while (i < n && j < m) {
            if (v1[i] > v2[j]) {
                ans.push_back(v1[i++]);
            }
            else if (v1[i] < v2[j]) {
                ans.push_back(v2[j++]);
