vector<int> findMaxSubsequnce(const vector<int>& v, int k) {
        vector<int> ans;
        for (int i = 0, n = size(v); i < n; ++i) {
            const int left = n - i;
            while (!empty(ans) && size(ans) + left > k && v[i] > ans.back()) {
                ans.pop_back();
