import java.math.BigInteger;
import java.util.ArrayList;
import java.util.Arrays;

class Leetcode {
    static boolean gt(ArrayList<Integer> a1, ArrayList<Integer> a2, int a1i, int a2i) {
        while (a1i < a1.size() && a2i < a2.size()) {
            if (a1.get(a1i) > a2.get(a2i))
                return true;
            if (a2.get(a2i) > a1.get(a1i))
                return false;
            a1i++;
            a2i++;
        }
        if (a1i >= a1.size())
            return false;
        else
            return true;
    }

    static BigInteger merge(ArrayList<Integer> a1, ArrayList<Integer> a2) {
        StringBuilder num = new StringBuilder();
        int i = 0, j = 0;
        while (i < a1.size() || j < a2.size()) {
            if (gt(a1, a2, i, j)) {
                num.append(a1.get(i++));
            } else {
                num.append(a2.get(j++));
            }
        }
        return new BigInteger(num.toString());
    }


    static ArrayList<Integer> buildMaxSegmentUtil(int[] a, int sz) {
        ArrayList<Integer> res = new ArrayList<>();
        int taken = 0;
        int i = 0;
        while (taken < sz) {
            int remain = sz - taken;
            int j = a.length - remain;
            int maxi = i;
            int max = -1;
            for (int k = i; k <= j; k++)
                if (a[k] > max) {
                    max = a[k];
                    maxi = k;
                }
            res.add(a[maxi]);
            i = maxi + 1;
            taken++;
        }
        return res;
    }

    static ArrayList<Integer>[] buildMaxSegment(int[] a, int k) {
        ArrayList<Integer>[] res = new ArrayList[k + 1];
        for (int i = 0; i <= k; i++) {
            if (i <= a.length)
                res[i] = buildMaxSegmentUtil(a, i);
        }
        return res;
    }


    public static int[] maxNumber(int[] x, int[] y, int k) {
        ArrayList<Integer>[] maxSegmentX = buildMaxSegment(x, k);
        ArrayList<Integer>[] maxSegmentY = buildMaxSegment(y, k);
        BigInteger mx = new BigInteger("-1");
        for (int xCnt = 0; xCnt <= k; xCnt++) {
            int yCnt = k - xCnt;
            if (xCnt <= x.length && yCnt <= y.length) {
                BigInteger tmp = merge(maxSegmentX[xCnt], maxSegmentY[yCnt]);
                if (tmp.compareTo(mx) > 0)
                    mx = tmp;
            }
        }
        String mxSt = mx.toString();
        int[] res = new int[k];
        for (int i = 0; i < k; i++)
            res[i] = mxSt.charAt(i) - '0';
        return res;
    }
}
