var maxNumber = function (nums1, nums2, k) {
    let max = Array(k).fill(0);

    const findMaxArray = (num, len) => {
        let pop = num.length - len,
            stack = [],
            i = 0;
        while (i < num.length) {
            while (pop && stack.length && stack[stack.length - 1] < num[i]) {
                stack.pop();
                pop--;
            }
            stack.push(num[i++]);
        }
        return stack.slice(0, len);
    };

    const greater = (num1, i, num2, j) => {
        while (i < num1.length && j < num2.length && num1[i] == num2[j]) {
            i++;
            j++;
        }
        return j == num2.length || num1[i] > num2[j];
    };

    const merge = (num1, num2) => {
        let i = 0,
            j = 0,
            combinedArr = [];
        while (i < num1.length || j < num2.length)
            combinedArr.push(greater(num1, i, num2, j) ? num1[i++] : num2[j++]);
        return combinedArr;
    };

    for (let i = Math.min(k, nums1.length); i >= 0 && k - i <= nums2.length; i--) {
        let combinedArray = merge(findMaxArray(nums1, i), findMaxArray(nums2, k - i));
        if (greater(combinedArray, 0, max, 0))
            max = combinedArray;
    }
    return max;
};

