        // 0 -> a 1 -> b
        int i=s1;
        int j=s2;
        while(i<a.length && j<b.length){
            if(a[i] == b[j]){
                i++;
                j++;
                continue;
            }
            if(a[i]<b[j])
                return 1;
            else
                return 0;
        }
        if(i == a.length)
            return 1;
