class Solution {
public:
    vector<int> maxNumber(vector<int>& a, vector<int>& b, int k) {
      int n=a.size();int m=b.size();
        vector<int> ans;
        for(int i=0;i<=k;i++)
        {
            if(i>n || k-i>m)continue;
            ans=max(ans,merge(findmax(a,i),findmax(b,k-i)));
        }
        return ans;
    }
    vector<int> findmax(vector<int> &a,int k)
    {
        vector<int> res;
        int to_pop=a.size()-k;
       for(int i=0;i<a.size();i++)
       {
           while(!res.empty() && to_pop && *res.rbegin()<a[i])
               res.pop_back(),to_pop--;
           res.push_back(a[i]);
       }
        res.resize(k);
        return res;
    }
    vector<int> merge(vector<int> a,vector<int> b)
    {
        vector<int> ans;
        auto s1=a.begin();
        auto e1=a.end();
        auto s2=b.begin();
        auto e2=b.end();
        while(s1!=e1 || s2!=e2)
            ans.push_back(lexicographical_compare(s1,e1,s2,e2)?*s2++:*s1++);
        return ans;
    }
};

