class Solution {
    public int[] maxNumber(int[] nums1, int[] nums2, int k) {
        int [] result = new int[k];
        if(k > nums1.length + nums2.length){
            return null;
        }
        String [][][] resultValues = getMaxNumber(nums1, nums2, k);
        String resultValue = resultValues[nums1.length][nums2.length][k];
        int i = 0;
        while(i < k){
            result[i] = resultValue.charAt(i++) - '0';
        }
        return result;
    }

    public String[][][] getMaxNumber(int[] nums1, int[] nums2, int k){
        String [][][] result = new String[nums1.length + 1][nums2.length + 1][k + 1];
        int m = nums1.length, n = nums2.length;

        //init
        Map<String, Integer> map1, map2;
        for(int h = 0; h < k + 1; h++){
            for(int i = 1; i < m + 1; i++){
                if(h == 0 || h > i){
                    result[i][0][h] = "";
                    continue;
                }
                map1 = findMax(Arrays.copyOfRange(nums1, m - i, m), i, 0, h);
                result[i][0][h] = map1.get("maxValue") + result[i - (map1.get("maxIndex") + 1)][0][h - 1];
            }
            for(int j = 1; j < n + 1; j++){
                if(h == 0 || h > j){
                    result[0][j][h] = "";
                    continue;
                }
                map2 = findMax(Arrays.copyOfRange(nums2, n - j, n), j, 0, h);
                result[0][j][h] = map2.get("maxValue") + result[0][j - (map2.get("maxIndex") + 1)][h - 1];
            }
            result[0][0][h] = "";
        }

        for(int h = 0; h < k + 1; h++){
            for(int i = 1; i < m + 1; i++){
                for(int j = 1; j < n + 1; j++){
                    if(h > i + j || h == 0){
                        result[i][j][h] = "";
                        continue;
                    }
                    map1 = findMax(Arrays.copyOfRange(nums1, m - i, m), i, j, h);
                    map2 = findMax(Arrays.copyOfRange(nums2, n - j, n), j, i, h);
                    if(map1.get("maxValue") == map2.get("maxValue")){
                        String tmp1 = result[i - (map1.get("maxIndex") + 1)][j][h - 1];
                        String tmp2 = result[i][j - (map2.get("maxIndex") + 1)][h - 1];
                        //get max of tmp1 and tmp2
                        for(int l = 0; l < tmp1.length(); l++){
                            if(tmp1.charAt(l) > tmp2.charAt(l)){
                                result[i][j][h] = map1.get("maxValue") + tmp1;
                                break;
                            } else if(tmp1.charAt(l) < tmp2.charAt(l)) {
                                result[i][j][h] = map2.get("maxValue") + tmp2;
                                break;
                            }
                        }
                        if(result[i][j][h] == null) result[i][j][h] = map1.get("maxValue") + tmp1;
                    } else if (map1.get("maxValue") > map2.get("maxValue")){
                        result[i][j][h] = map1.get("maxValue") + result[i - (map1.get("maxIndex") + 1)][j][h - 1];
                    } else {
                        result[i][j][h] = map2.get("maxValue") + result[i][j - (map2.get("maxIndex") + 1)][h - 1];
                    }
                }
            }
        }

        return result;

    }

    public Map<String, Integer> findMax(int[] nums, int m, int n, int k){
