            for(int i = 1; i < m + 1; i++){
                for(int j = 1; j < n + 1; j++){
                    if(h > i + j || h == 0){
                        result[i][j][h] = "";
                        continue;
                    }
                    map1 = findMax(Arrays.copyOfRange(nums1, m - i, m), i, j, h);
