        for(int i = maxIndex + 1; i < Math.min(m, m + n - k + 1); i++){
            if(nums[i] > maxValue){
                maxIndex = i;
                maxValue = nums[i];
            }
        }
        map.put("maxIndex", maxIndex);
        map.put("maxValue", maxValue);
        return map;
    }
}
