#include <unordered_map>
#include <stdio.h>
using namespace std;

class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        int wordLength = wordLength = words[0].length(); //each word in the words vector is the same length, so we can just check the first entry. Normally I would check the size of the vector before writing a line of code like this to prevent a seg fault, but the conditions of this test gauruntee the vector size will be at least 1 or greater
        int numberOfWords = words.size();
        vector<int> resultIndices; //we'll use this to store the results that this function will return

        //copy the vector of words into an unordered_map. This map will contain a count of how many times that word appears in the words vector
        unordered_map<string, int> wordBankMap;
        for (int i = 0; i < words.size(); i++) {
            if (wordBankMap.find(words[i]) == wordBankMap.end()) {
                wordBankMap[words[i]] = 1; //if it's the first time this string has been added, set the counter to 1
            }
            else {
                wordBankMap[words[i]] = wordBankMap[words[i]] + 1; //otherwise, increment the counter, because the vector of words can contain repeated words
            }
        }


        /*this is a simple check to return nothing in the case that the "s" string is not
        sufficiently long enough to check for a concatenation of the words in the vector of words*/
        if (wordLength * numberOfWords > s.length()) {
            return resultIndices;
        }

        //iterate through the whole s string, a wordLength at a time
        for (int idx = 0; idx <= s.length() - (numberOfWords * wordLength); idx++) {
            unordered_map<string, int> wordsUsedMap;

            //now we're checking a subset to see if it matches a concatenation of the words in the words vector
            for (int chunkIdx = idx; chunkIdx < idx + (numberOfWords * wordLength); chunkIdx += wordLength) {
                //check to see if that chunk matches a word in the vector of words
                if (wordBankMap.find(s.substr(chunkIdx, wordLength)) == wordBankMap.end()) { //in this case, it doesn't match
                    break;
                }
                else if (wordsUsedMap.find(s.substr(chunkIdx, wordLength)) == wordsUsedMap.end()) { //in this case, the word exists in the wordBankMap, but it hasn't been accounted for in our wordsUsedMap
                    wordsUsedMap[s.substr(chunkIdx, wordLength)] = 1; //assign the counter to 1
                }
                else { //in this case, there already has been at least 1 entry into the wordsUsedMap, so now we just increment the counter and check if we went past our allowed count
                    wordsUsedMap[s.substr(chunkIdx, wordLength)] = wordsUsedMap[s.substr(chunkIdx, wordLength)] + 1;
                    if (wordsUsedMap[s.substr(chunkIdx, wordLength)] > wordBankMap[s.substr(chunkIdx, wordLength)]) {
                        break; //we went past our allotted count, so we can break out of this loop and move on
                    }
                }
            }

            if (wordsUsedMap == wordBankMap) { //if what was used matches what's in the word bank, we have a winner!
                resultIndices.push_back(idx); //add to the results
            }
        }

        return resultIndices;
    }
};
