class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        vector<int> result;
        int n = s.length();
        int numWords = words.size();
        if (n == 0 || numWords == 0) {
            return result;
        }

        int wordLen = words[0].length();
        int totalLen = numWords * wordLen;
        unordered_map<string, int> wordCount; // Stores the count of each word

        for (const string& word : words) {
            wordCount[word]++;
        }

        for (int i = 0; i < wordLen; ++i) {
            int left = i, right = i, count = 0;
            unordered_map<string, int> currentCount; // Stores count of words in current window

            while (right + wordLen <= n) {
                string currentWord = s.substr(right, wordLen);
                right += wordLen;

                if (wordCount.find(currentWord) != wordCount.end()) {
                    currentCount[currentWord]++;
                    count++;

                    while (currentCount[currentWord] > wordCount[currentWord]) {
                        string leftWord = s.substr(left, wordLen);
                        left += wordLen;
                        currentCount[leftWord]--;
                        count--;
                    }

                    if (count == numWords) {
                        result.push_back(left);
                    }
                } else {
                    currentCount.clear();
                    count = 0;
                    left = right;
                }
            }
        }

        return result;
    }
};
