class Solution {
    int wordIdPtr=0;
    vector<int> wordIdCount;

    struct Trie {
        Trie *failure;
        int id=-1;
        Trie *next[26];
        Trie () {
            memset(next,0,sizeof(next));
        }
    }*root;
    void buildTrie (vector<string>& dict) {
        root = new Trie();
        for (auto it : dict) {
            Trie *p=root;
            for (int i=0; i<it.length(); i++) {
                if (p->next[it[i]-'a'] == nullptr)
                    p->next[it[i]-'a'] = new Trie();
                p=p->next[it[i]-'a'];
            }
            if (p->id == -1)
                p->id = wordIdPtr++;
            wordIdCount[p->id]++;
        }
        queue<Trie*> Q;
        for (int i=0; i<26; i++) {
            if (root->next[i]) {
                root->next[i]->failure = root;
                Q.push(root->next[i]);
            }else
                root->next[i] = root;
        }
        while (!Q.empty()) {
            Trie *x = Q.front(); Q.pop();
            for (int i=0; i<26; i++)
                if (x->next[i]) {
                    Trie *seek=x->failure;
                    while (!seek->next[i])
                        seek = seek->failure;
                    x->next[i]->failure = seek->next[i];
                    if (seek->next[i]->id != -1)
                        x->next[i]->id = seek->next[i]->id;
                    Q.push(x->next[i]);
                }
        }
    }
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        vector<int> ans;
        if (s.length()==0 || words.size()==0 || words[0].size()==0)
            return ans;
        wordIdCount.assign(words.size(), 0);
        buildTrie(words);

        //find all words in s. O(s.length() + wordlen*wordcount)
        vector<int> sid(s.length());
        Trie *p=root;
        for (int i=0; i<s.length(); i++) {
            int chIdx = s[i]-'a';
            while (!p->next[chIdx])
                p=p->failure;
            p = p->next[chIdx];
            sid[i]=p->id;
            cout<<setw(2)<<sid[i]<<" ";
        }
        cout<<" << Assigned ID"<<endl;
        for (int i=0; i<s.length(); i++)
            cout<<setw(2)<<s[i]<<" ";
        cout<<" << string"<<endl;
        for (int i=0; i<s.length(); i++)
            cout<<setw(2)<<i<<" ";
        cout<<" << index"<<endl;

        //getAnswer
        int wlen = words[0].length();
        for (int offset=wlen-1; offset<wlen-1+wlen; offset++) {
            auto idx = [&](int k) {return offset+k*wlen;};
            int count=0;
            //each item at most push and pop once.
            vector<queue<int>> appearPos(wordIdPtr);
            for (int i=0; idx(i)<s.length(); i++)
                if(sid[idx(i)]!=-1) {
                    int nowSid = sid[idx(i)];
                    if (appearPos[nowSid].size()+1 > wordIdCount[nowSid]) {
                        int stopborder=i-count;
                        for (int j=appearPos[nowSid].front(); j >= stopborder; j--) {
                            appearPos[sid[idx(j)]].pop(), count--;
                        }
                    }
                    appearPos[nowSid].push(i), count++;
                    cout<<"\twork at: "<<idx(i)<<" count="<<count<<endl;
                    if (count == words.size()) {
                        ans.push_back(idx(i)-words.size()*wlen+1);
                        cout<<"Got Ans At="<<idx(i)<<endl;
                    }
                }else if (count){
                    count=0;
                    appearPos.assign(wordIdPtr, queue<int>());
                }
        }
        return ans;
    }
};
