#This is the solution 1............this is the fastest solution with 52 ms speed..

class Solution:
    def findSubstring(self, s: str, words: List[str]) -> List[int]:
        if not s or not words:
            return []

        count = {}
        for word in words:
            count[word] = count.get(word,0) + 1

        lw = len(words[0])
        fin = []

        for i in range(lw):
            l = r = i
            window = {}

            while r < len(s):
                curr_w = s[r:r+lw]

                if curr_w not in count:
                    window.clear()
                    r += lw
                    l = r
                else:
                    window[curr_w] = window.get(curr_w,0) + 1

                    if window[curr_w] <= count[curr_w]:
                        r += lw

                    else:
                        while l <= r and window[curr_w]>count[curr_w]:
                            w = s[l:l+lw]
                            window[w] -= 1
                            l += lw
                        r += lw

                    if window == count:
                            fin.append(l)
        return fin
