```class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        int len=words[0].size();
        int noofWords=words.size();
        vector<int> ans;
        unordered_map<string,int> umap;
        if(len*noofWords>s.length())
        {
            return ans;
        }
        for(string word:words)
        {
            umap[word]++;
        }
        for(int i=0;i<=s.length()-(noofWords*len);i++)
        {
            unordered_map<string,int> mp;
            for(int j=i;j<i+(noofWords*len);j+=len)
            {
                string str=s.substr(j,len);
                if(umap.find(str)==mp.end())break;
                else
                {
                    mp[str]++;
                }
                if(mp[str]>umap[str])break;
            }
            if(mp==umap)ans.push_back(i);
        }
        return ans;
    }

};
