class Solution {
public:
  vector<int> findSubstring(string s, vector<string>& words){
    unordered_map<string,pair<int,int>>mp;
    for(auto &w : words) mp[w].first++,mp[w].second++;

    vector<int> answer;
    int n = words[0].size(), N = words.size();
    int limit = s.size() - n * N + 1;
    if(limit < 0) return answer;

    for(int i = 0; i != limit; i++){
      int tN = 0, j = i;
      for(; ;j += n){
        string tmp = s.substr(j,n);
        if(mp[tmp].first) tN++, mp[tmp].first--;
        else break;
      }
      if(tN == N) answer.push_back(i);

      for(auto &it: mp) it.second.first = it.second.second;
    }

  return answer;     
  }
};
