class Solution {
public:
    vector<int> findSubstring(string str, vector<string>& words) {
        if (str.empty() or words.empty()) {return vector<int> ();}
        if (str.size() < words.size()*words[0].size()) {return vector<int> ();}
        vector<int> resultIndices;
        unordered_map<string, int> wordFrequencyMap;
        for (auto word : words) {
          wordFrequencyMap[word]++;
        }
        int wordsCount = words.size(), wordLength = words[0].length();

        for (int i = 0; i <= str.length() - wordsCount * wordLength; i++) {
          unordered_map<string, int> wordsSeen;
          for (int j = 0; j < wordsCount; j++) {
            int nextWordIndex = i + j * wordLength;
            string word = str.substr(nextWordIndex, wordLength);
            if (!wordFrequencyMap.count(word)) { 
              break;
            }
            wordsSeen[word]++;  
            if (wordsSeen[word] > wordFrequencyMap[word]) {
              break;
            }
            if (j + 1 == wordsCount) { 
              resultIndices.push_back(i);
            }
          }
        }

        return resultIndices;    
    }
};

