class Solution {
    public List<Integer> findSubstring(String s, String[] words) {
        List<Integer> answer = new ArrayList<>();

        int wordLength = words[0].length();
        int concatenatedStringLength = words.length * wordLength;

        if(s.length() < concatenatedStringLength) return answer;

        Map<String, Integer> freqMap = new HashMap<>();
        for(String word: words) {
            freqMap.put(word, freqMap.getOrDefault(word, 0) + 1); 
        }

        for(int i=0;i<=s.length()-concatenatedStringLength;i++) {
            Map<String, Integer> tempMap = new HashMap<>();
            boolean found = true;
            for(int j=i; j < i + concatenatedStringLength; j+=wordLength) {
                String substr = s.substring(j, j+wordLength);
                if(!freqMap.containsKey(substr)) {
                    found = false;
                    break;
                }
                tempMap.put(substr, tempMap.getOrDefault(substr, 0) + 1);
            }

            if(found) {
                for(Map.Entry<String, Integer> entry: tempMap.entrySet()) {
                    if(entry.getValue().intValue() != freqMap.get(entry.getKey()).intValue()) {
                        found = false;
                        break;
                    }
                }
            }

            if(found) answer.add(i);
        }
        return answer;
    }

}
