class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        vector<int> v;
        unordered_map<string,int> m;
        for(auto x:words)
            m[x]++;

        int l=words[0].length();
        int len=words.size()*l;
        int temp=s.length()-len;
        if(len<0)// none is possible
            return v;
        for(int i=0;i<=temp;i++)
        {
            int c=fun(s,m,i,i+len,l);
            if(c==words.size())
                v.push_back(i);
        }
        return v;
    }
    int fun(string& s,unordered_map<string,int>& m,int i,int len,int l)
    {
        if(i==len)
            return 0;

        string x=s.substr(i,l);
        int t;
        if(m.find(x)!=m.end() && m[x]>0)
        {
            m[x]--;
            t=1+fun(s,m,i+l,len,l);
            m[x]++;
        }
        else
            t=0;
        return t;
    }
};
