class Solution {
public:
    bool found(unordered_map<string, int> ump, string curr, int wordsize)
    {
        for(int i = 0 ; i < curr.size() ; i+=wordsize)
        {
            if(!ump[curr.substr(i, wordsize)]) return false;
            ump[curr.substr(i, wordsize)]--;
        }
        return true;
    }


    vector<int> findSubstring(string s, vector<string>& words) {
        vector<int>indexes;
        unordered_map<string, int>ump;
        int no_of_words = words.size();
        int len_of_words = no_of_words*words[0].length();
        if(s.size() < len_of_words) return {};
        for(auto it : words) ump[it]++;

        for(int i = 0 ; i <= s.size()-len_of_words ; i++)
        {
            if(found(ump, s.substr(i, len_of_words), words[0].size())) indexes.push_back(i);
        }
        return indexes;
    }
};
