    vector<int> findSubstring(string s, vector<string>& words) 
    {

        //DEFINE WINDOW LENGTH;
        int n = words.size();
        int slen = s.length();
        int wordlen = words[0].length();
        int windowsize = n*wordlen;



        //put in map
        unordered_map<string,int> m;
        for(int i=0;i<n;i++)
        {
            m[words[i]]++;
        }

        if(slen==windowsize && m.size()==1)
        {
            return {0};
        }

        int si = 0;
        int ei = 0;

        unordered_map<string,int> temp;

        vector<int> ans;

        while(ei<slen)
        {
            if((ei-si+1)<windowsize)
            {
                ei++;
            }
            else if((ei-si+1)==windowsize)
            {
                //do some work
                temp = m;
                string t = s.substr(si,ei-si+1);


                int i =0;

                while(i<t.length())
                {
                    string sub = t.substr(i,wordlen);
                    if(temp.find(sub)!=temp.end())
                    {
                        temp[sub]--;
                    }
                    if(temp[sub]==0)
                    {
                        temp.erase(sub);
                    }
                    i = i + wordlen;
                }
                if(temp.size()==0)
                {
                    ans.push_back(si);
                }
                si = si+1;
                ei = ei+1;
            }
        }
        return ans;
