            return;

        for (int i = 0; i < length; ++i)
        {
            if (board[0][i] == 'O')
                bfsBoundary(board, 0, i);

            if (board[width-1][i] == 'O')
                bfsBoundary(board, width-1, i);
        }

        for (int i = 0; i < width; ++i)
        {
            if (board[i][0] == 'O')
                bfsBoundary(board, i, 0);
            if (board[i][length-1] == 'O')
                bfsBoundary(board, i, length-1);
        }

