        void check(vector<vector<char>>& board, int i, int j) {
            if (board[i][j] == 'O') {
                board[i][j] = '1';
                if (i > 1) check(board, i - 1, j);
                if (j > 1) check(board, i, j - 1);
                if (i + 1 < board.size()) check(board, i + 1, j);
                if (j + 1 < board[0].size()) check(board, i, j + 1);
            }
        }
    };

