## **Union Find**
```cpp
class UnionFind {
public:
    vector<int> parent;

    UnionFind(vector<vector<char>>& board, int m ,int n) {
        // Add dummy node to the tail
        for(int i=0; i<m*n+1; ++i) {
            parent.push_back(i);
        }
    }

    int find(int i) {
        if(i == parent[i]) return i;
        return find(parent[i]);
    }

    void unite(int i, int j) {
        int pi = find(i);
        int pj = find(j);
        if(pi != pj) parent[pj] = pi;
    }

    bool isConnect(int i, int j) {
        return find(i) == find(j);
    }
};

class Solution {
public:
    void solve(vector<vector<char>>& board) {
        if(!board.size()) return;

        int rows = board.size();
        int cols = board[0].size();

        UnionFind uf = UnionFind(board, rows, cols);
        int dummy = rows * cols;

        for(int i=0; i<rows; ++i) {
            for(int j=0; j<cols; ++j) {
                if(board[i][j]=='O') {
                    // If 'O' is on boarder, connect to dummy node
                    if(i==0 || i==rows-1 || j==0 || j==cols-1){
                        uf.unite(i*cols+j, dummy);
                    }
                    // Else, connect surrounded 'O'
                    else {
                        if(i>0 && board[i-1][j]=='O') uf.unite(i*cols+j, (i-1)*cols+j);
                        if(i<rows-1 && board[i+1][j]=='O') uf.unite(i*cols+j, (i+1)*cols+j);
                        if(j>0 && board[i][j-1]=='O') uf.unite(i*cols+j, i*cols+j-1);
                        if(j<cols-1 && board[i][j+1]=='O') uf.unite(i*cols+j, i*cols+j+1);
                    }
                }
            }
        }

        // Go through whole board check each 'O' if it is connected to dummy node
        // If no, mark as 'X'
        for(int i=0; i<rows; ++i) {
            for(int j=0; j<cols; ++j) {
                if(board[i][j]=='O'){
                    if(uf.isConnect(i*cols+j, dummy) == false) board[i][j] = 'X';
                }
            }
        }
    }
};
```

