class Solution:
    def solve(self, board: List[List[str]]) -> None:
        m, n = len(board), len(board[0])
        DIR = [0, 1, 0, -1, 0]
        protected = [[False] * n for _ in range(m)]

        def dfs(r, c):
            if r < 0 or r == m or c < 0 or c == n or board[r][c] == "X" or protected[r][c]:
                return

            protected[r][c] = True
            for i in range(4):
                dfs(r + DIR[i], c + DIR[i + 1])

        for r in range(m):
            dfs(r, 0)
            dfs(r, n - 1)

        for c in range(n):
            dfs(0, c)
            dfs(m - 1, c)

        for r in range(m):
            for c in range(n):
                if board[r][c] == "O" and not protected[r][c]:
                    board[r][c] = "X"
