class Solution {
public:

    int dx[4] = {0, 0, 1, -1};
    int dy[4] = {1, -1, 0, 0};

    bool is_valid(vector<vector<char>>& board, int x, int y, int n, int m, vector<vector<bool>>& vis){

        if(x>=0 and x<n and y >= 0 and y < m and board[x][y] == 'O' and !vis[x][y])
            return true;

        return false;
    }

    void fill_comp(int x, int y, vector<vector<char>>& board, vector<vector<bool>>& vis){
        vis[x][y] = true;
        board[x][y] = 'X';

        int n = board.size();
        int m = board[0].size();

        for(int i{}; i<4; ++i){
            int xx = x+dx[i];
            int yy = y+dy[i];

            if(is_valid(board, xx, yy, n, m, vis)){

                fill_comp(xx, yy, board, vis);
            }
        }
    }

    void mark_bad(vector<vector<bool>>&vis, vector<vector<char>>& board, int x, int y){

        vis[x][y] = true; //marking bad means already making it visited so that it won't get visited while doing main dfs
        int n = board.size();
        int m = board[0].size();

        for(int i{}; i<4; ++i){
            int xx = x + dx[i];
            int yy = y + dy[i];

            if(is_valid(board, xx, yy, n, m, vis)){

                mark_bad(vis, board, xx, yy);
            }
        }

    }

    void solve(vector<vector<char>>& board) {

        int n = board.size();
        int m = board[0].size();
        vector<vector<bool>>vis(n, vector<bool>(m, 0));

		// Traversing the boundary and marking bad components
        for(int i{}; i<n; ++i){
            if(board[i][0] == 'O' and !vis[i][0]){
                mark_bad(vis, board, i, 0);
            }

            if(board[i][m-1] == 'O' and !vis[i][m-1]){
                mark_bad(vis, board, i, m-1);
            }
        }

        for(int j{}; j<m; ++j){

            if(board[0][j]=='O' and !vis[0][j]){
                mark_bad(vis, board, 0, j);
            }
            if(board[n-1][j] == 'O' and !vis[n-1][j]){
                mark_bad(vis, board, n-1, j);
            }
