		//main dfs fill_comp that will fill good components of "O" with "X"
        for(int i{}; i<n; ++i){
            for(int j{}; j<m; ++j){

                if(board[i][j] == 'O' and !vis[i][j]){

                    fill_comp(i, j, board, vis);
                }
            }
        }
    }
};
