class Solution {

    // Union Find 
    // Intuition: 
    //       - We need to separate 'O' in the borders from the other 'O'
    //       - So, if a node 'O' is on the border connect it to the virtual node.
    //       - All the others 'O' connect to their 'O' neighbors. (eventually, if they are tied to a border 'O', they will connect)
    //       - After that, traverse the board, flipping the 'O' *** NOT *** connected to the virtual.

    public void solve(char[][] board) {
        if(board == null || board.length == 0) return;
        int row = board.length;
        int col = board[0].length;
        UnionFind uf = new UnionFind(row, col);
        for(int i=0; i<row; i++){
            for(int j=0; j<col; j++){

                //Is boarder? Is 'O'? ... connect to the virtual.
                if((i == 0 || j == 0 || i == row - 1 || j == col - 1) && (board[i][j] == 'O')){
                    uf.union(uf.virtualNode, index(col, i, j)); //connect to virtual
                    continue;
                } 

                //not border, but 'O'
                if(board[i][j] == 'O') {
                    int id = index(col, i, j);
                    // connect the neighbors
                    if(i + 1 < row && board[i + 1][j] == 'O') uf.union(id, index(col, i + 1, j));
                    if(i - 1 >=  0 && board[i - 1][j] == 'O') uf.union(id, index(col, i - 1, j));
                    if(j + 1 < col && board[i][j + 1] == 'O') uf.union(id, index(col, i, j + 1));
                    if(j - 1 >=  0 && board[i][j - 1] == 'O') uf.union(id, index(col, i, j - 1));
                }
            }
        }

        for(int i=0; i<row; i++)
            for(int j=0; j<col; j++)
                if(board[i][j] == 'O' && !uf.connected(index(col, i, j), uf.virtualNode)) 
                    board[i][j] = 'X';
    }

    class UnionFind{
        int[] parent;
        int[] size;
        int virtualNode;

        UnionFind(int N, int M){
            virtualNode = N * M;
            parent = new int[N * M + 1]; //extra for the virtual Node
            size = new int[N * M + 1]; 
            for(int i=0; i< N * M +1; i++){
                parent[i] = i;
                size[i] = 1;
            }
        }

        int find(int i){
            while(i != parent[i]){
                parent[i] = parent[parent[i]]; // compress
                i = parent[i];
            }
            return i;
        }
        boolean connected(int p, int q){
            return find(p) == find(q);
        }
        void union(int p, int q){
            int rootP = find(p);
            int rootQ = find(q);
            if(rootP == rootQ) return; //the same parent

            if(size[rootP] < size[rootQ]){
                parent[rootP] = rootQ;
                size[rootQ] += size[rootP];
            }else{
                parent[rootQ] = rootP;
                size[rootP] += size[rootQ];
            }
        }
    }
    int index(int col, int i, int j){
        return i * col + j;
    }
}
