    class UnionFind{
        int[] parent;
        int[] size;
        int virtualNode;

        UnionFind(int N, int M){
            virtualNode = N * M;
            parent = new int[N * M + 1]; //extra for the virtual Node
            size = new int[N * M + 1]; 
            for(int i=0; i< N * M +1; i++){
                parent[i] = i;
                size[i] = 1;
            }
        }

        int find(int i){
            while(i != parent[i]){
                parent[i] = parent[parent[i]]; // compress
                i = parent[i];
            }
            return i;
        }
        boolean connected(int p, int q){
            return find(p) == find(q);
        }
        void union(int p, int q){
            int rootP = find(p);
            int rootQ = find(q);
            if(rootP == rootQ) return; //the same parent

            if(size[rootP] < size[rootQ]){
                parent[rootP] = rootQ;
                size[rootQ] += size[rootP];
            }else{
                parent[rootQ] = rootP;
                size[rootP] += size[rootQ];
            }
        }
    }
    int index(int col, int i, int j){
        return i * col + j;
    }
