// the border
private void detectConnected(int x, int y, char[][] board) {

	if (x < 0 || x >= board.length || y < 0 || y >= board[0].length || visited[x][y] || board[x][y] != 'O') {
		return;
	}

	visited[x][y] = true;

	detectConnected(x + 1, y, board);
	detectConnected(x, y + 1, board);
	detectConnected(x - 1, y, board);
	detectConnected(x, y - 1, board);
