class DisjointSet{
    private:
        int* parent;
    public:
        DisjointSet(int size){
            parent = new int[size];
            for(int i = 0; i < size; i++)
                    parent[i] = i;
        }
        ~DisjointSet(){
            delete [] parent;
        }
        void Union(int p, int q){
            int pRoot = Find(p);
            int qRoot = Find(q);
            parent[pRoot] = qRoot;
        }
        int Find(int p){
            while(parent[p] != p)
                p = parent[p];
            return p;
        }
};

class Solution {
public:
    void solve(vector<vector<char>>& board) {
        int rows = board.size();
        if(rows == 0) return ;
        int cols = board[0].size();
        if(rows == 1 || cols == 1)return;
        DisjointSet ds(rows * cols);
		// #1 Union adjacents 'O's
        for(int i = 1 ; i < rows - 1; i++){
            for(int j = 1; j < cols -1; j++){
                if(board[i][j] == 'O')
                {
                    int curPos = i * cols + j;
                    if(board[i-1][j] == 'O')
                        ds.Union(curPos, (i - 1) * cols + j);
                    if(board[i+1][j] == 'O')
                        ds.Union(curPos, (i + 1) * cols + j);
                    if(board[i][j - 1] == 'O')
                        ds.Union(curPos, curPos - 1);
                    if(board[i][j + 1] == 'O')
                        ds.Union(curPos, curPos + 1);
                }
            }
        }
		// #2 Record border "O" 's set
        set<int> borderSetRoot;
        for(int i = 0 ; i < rows; i+=rows-1)
            for(int j = 0; j < cols; j++)
                if(board[i][j] == 'O')
                {

                    borderSetRoot.insert(ds.Find( i * cols + j));
                }
        for(int i = 1 ; i < rows - 1; i++)
            for(int j = 0; j < cols; j+=cols-1)
                if(board[i][j] == 'O')
                {

                    borderSetRoot.insert(ds.Find( i * cols + j));
                }

        // #3 Flip "O"s not in border set
        for(int i = 1 ; i < rows - 1; i++){
            for(int j = 1; j < cols -1; j++){
                if(board[i][j] == 'O')
                {
                    int curPos = i * cols + j;                   
                    if(borderSetRoot.find(ds.Find(curPos)) == borderSetRoot.end())
                        board[i][j] = 'X';
                }
            }
        }

    }
};
