    # Helper function to determine if a postiion is on the border of the matrix
    def is_border(self, row, col, m, n):
      return row == 0 or row == m - 1 or col == 0 or col == n-1

    # Helper function to get valid neighbours
    def get_valid_neighbours(self, row, col, board):
      valid_directions = []
      for drow, dcol in [(-1,0),(1,0),(0,-1),(0,1)]:
        new_row, new_col = row + drow, col + dcol
        if 0 <= new_row < len(board) and 0 <= new_col < len(board[0]) and board[new_row][new_col] == 'O':
          valid_directions.append((new_row, new_col))
      return valid_directions

    # Perform DFS 
    def dfs(self, row, col, board):
      board[row][col] = 'E'
      # Get Valid rows before performing DFS to reduce recursive stack (Optimization)
      for row,col in self.get_valid_neighbours(row, col, board):
        self.dfs(row, col, board)


    def solve(self, board: List[List[str]]) -> None:
        """
        Do not return anything, modify board in-place instead.
        """
        m,n = len(board), len(board[0])
        # Perform DFS from all nodes at the border and mark as "escaped (E)" as they are not "surrounded" by X
        for row in range(m):
          for col in range(n):
            if self.is_border(row, col, m, n) and board[row][col] == 'O':
                self.dfs(row, col, board)

        # Iterate through all elements again and change the "E" elements back to "O" and everything else to 'X'
        for row in range(m):
          for col in range(n):
            if board[row][col] == 'O': 
              board[row][col] = 'X'
            elif board[row][col] == 'E':
              board[row][col] = 'O'

