class Solution {
public:
    void solve(vector<vector<char>>& board) {
        int m = board.size();
        int n = board[0].size();
        vector<vector<int>> visited(m , vector<int>(n, 0));
        queue <pair<int, int>> q;
        for (int i = 0; i < n; i++) {
            if (board[0][i] == 'O') {
                q.push({0, i});
                visited[0][i] = 1;
            }
            if (board[m-1][i] == 'O') {
                q.push({m-1, i});
                visited[m-1][i] = 1; 
            }   
        }
        for (int i = 0; i < m; i++) {
            if (board[i][0] == 'O') {
                q.push({i, 0});
                visited[i][0] = 1;
            }
            if (board[i][n-1] == 'O') {
                q.push({i, n-1}); 
                visited[i][n-1] = 1; 
            } 
        }
        int drow[] = {-1, 0, +1, 0};
        int dcol[] = {0, +1, 0, -1};
        while (!q.empty()) {
            int row = q.front().first;
            int col = q.front().second;
            q.pop();
            for (int i = 0; i < 4; i++) {
                    int nrow = row + drow[i];
                    int ncol = col + dcol[i];
                    if (nrow >= 0 && nrow < m && ncol >= 0 && ncol < n &&
                        board[nrow][ncol] == 'O' && visited[nrow][ncol] == 0) {
                            q.push({nrow, ncol});
                            visited[nrow][ncol] = 1;
                        }
                }
        }
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (board[i][j] == 'O' && visited[i][j] == 0)
                    board[i][j] = 'X';
            }
        }
    }
};
