class Solution {
public:
    void dfs(vector<vector<char>> &board, vector<vector<bool>> &visited, int i, int j) {
        int m = board.size();
        int n = board[0].size();

        if (!visited[i][j] && board[i][j] == 'O') {
            visited[i][j] = true;
            if (i - 1 >= 0)
                dfs(board, visited, i - 1, j);
            if (j - 1 >= 0)
                dfs(board, visited, i, j - 1);

            if (i + 1 < m)
                dfs(board, visited, i + 1, j);
            if (j + 1 < n)
                dfs(board, visited, i, j + 1);
        }
    }


    void solve(vector<vector<char>> &board) {
        int m = board.size();
        int n = board[0].size();
        // cout<<m<<" "<<n<<endl;

        vector<vector<bool>> visited(m, vector<bool>(n, false));

        for (int i = 0; i < m; i++) {
            dfs(board, visited, i, 0);
            dfs(board, visited, i, n - 1);
        }
        for (int j = 0; j < n; j++) {
            dfs(board, visited, 0, j);
            dfs(board, visited, m - 1, j);
        }

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (!visited[i][j])
                    board[i][j] = 'X';
            }
        }
    }
};
```

2. BFS approach: 

```
class Solution {
public:
    void bfs(vector<vector<char>> &board, vector<vector<bool>> &visited) {
        int m = board.size();
        int n = board[0].size();
        queue<pair<int, int>> q;

        //border 'O'
        for (int i = 0; i < m; i++) {
            if (board[i][0] == 'O')
                q.push({i, 0});
            if (board[i][n - 1] == 'O')
                q.push({i, n - 1});
        }

        //border 'O'
        for (int j = 1; j < n - 1; j++) {
            if (board[0][j] == 'O')
                q.push({0, j});
            if (board[m - 1][j] == 'O')
                q.push({m - 1, j});
        }

        while (!q.empty()) {
            int i = q.front().first;
            int j = q.front().second;
            visited[i][j] = true;
            q.pop();

            //up
            if (i - 1 >= 0 && board[i - 1][j] == 'O' && !visited[i - 1][j])
                q.push({i - 1, j});
            //down
            if (i + 1 < m && board[i + 1][j] == 'O' && !visited[i + 1][j])
                q.push({i + 1, j});
            // left
            if (j - 1 >= 0 && board[i][j - 1] == 'O' && !visited[i][j - 1])
                q.push({i, j - 1});
            //right
            if (j + 1 < n && board[i][j + 1] == 'O' && !visited[i][j + 1])
                q.push({i, j + 1});
        }
    }


    void solve(vector<vector<char>> &board) {
        int m = board.size();
        int n = board[0].size();

        vector<vector<bool>> visited(m, vector<bool>(n, false));

        bfs(board, visited);

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (!visited[i][j])
                    board[i][j] = 'X';
            }
        }
    }
};
```

