class Solution {
public:
    void solve(vector<vector<char>>& board) {
        int aHeight = board.size();
        if (aHeight <= 2) { return; }

        int aWidth = board[0].size();
        if (aWidth <= 2) { return; }

        bool aVisited[aHeight][aWidth];
        memset(aVisited, false, sizeof(aVisited));

        int aStackX[aWidth * aHeight + 1];
        int aStackY[aWidth * aHeight + 1];

        int aHitX[aWidth * aHeight + 1];
        int aHitY[aWidth * aHeight + 1];

        int aStackCount = 0;
        int aHitCount = 0;

        bool aHitEdge = false;

        int aPopX = 0, aPopY = 0, aCheckX = 0, aCheckY = 0;

        for (int i=0;i<aWidth;i++) {
            for (int n=0;n<aHeight;n++) {
                if (aVisited[n][i] == false && board[n][i] == 'O') {

                    aHitCount = 0;

                    aStackCount = 1;
                    aHitEdge = false;

                    aStackX[0] = i;
                    aStackY[0] = n;

                    aVisited[n][i] = true;

                    while (aStackCount > 0) {

                        aStackCount -= 1;
                        aPopX = aStackX[aStackCount];
                        aPopY = aStackY[aStackCount];

                        aHitX[aHitCount] = aPopX;
                        aHitY[aHitCount] = aPopY;
                        aHitCount += 1;

                        if (aPopX == 0 || aPopX == (aWidth - 1)) { aHitEdge = true; }
                        if (aPopY == 0 || aPopY == (aHeight - 1)) { aHitEdge = true; }

                        if (aPopX > 0 && aVisited[aPopY][aPopX-1] == false && board[aPopY][aPopX-1] == 'O') {
                            aVisited[aPopY][aPopX-1] = true;
                            aStackX[aStackCount] = aPopX - 1;
                            aStackY[aStackCount] = aPopY;
                            aStackCount += 1;
                        }

                        if (aPopX < (aWidth - 1) && aVisited[aPopY][aPopX+1] == false && board[aPopY][aPopX+1] == 'O') {
                            aVisited[aPopY][aPopX+1] = true;
                            aStackX[aStackCount] = aPopX + 1;
                            aStackY[aStackCount] = aPopY;
                            aStackCount += 1;
                        }

                        if (aPopY > 0 && aVisited[aPopY-1][aPopX] == false && board[aPopY-1][aPopX] == 'O') {
                            aVisited[aPopY-1][aPopX] = true;
                            aStackX[aStackCount] = aPopX;
                            aStackY[aStackCount] = aPopY - 1;
                            aStackCount += 1;
                        }

                        if (aPopY < (aHeight - 1) && aVisited[aPopY+1][aPopX] == false && board[aPopY+1][aPopX] == 'O') {
                            aVisited[aPopY+1][aPopX] = true;
                            aStackX[aStackCount] = aPopX;
                            aStackY[aStackCount] = aPopY + 1;
                            aStackCount += 1;
                        }
                    }

                    if (aHitEdge == false) {
                        for (int k=0;k<aHitCount;k++) {
                            board[aHitY[k]][aHitX[k]] = 'X';
                        }
                    }
                }
            }
        }        
    }
};
