class Solution {
public:
    void solve(vector<vector<char>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        vector<vector<char>> temp = grid;

        for(int i=0;i<m;i++){

            if(temp[i][0] == 'O')
                bfs1(i,0,temp,grid);

            if(temp[i][n-1] == 'O')
                bfs1(i,n-1,temp,grid);
        }
        for(int i=0;i<n;i++){

            if(temp[0][i] == 'O')
                bfs1(0,i,temp,grid);

            if(temp[m-1][i] == 'O')
                bfs1(m-1,i,temp,grid);

        }

        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(temp[i][j] == 'O'){
                    temp[i][j] = 'X';
                }
            }
        }

        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(temp[i][j] == '1')
                    temp[i][j] = 'O';
            }
        }
        grid = temp;
    }

    void bfs1(int i, int j, vector<vector<char>>& vis,vector<vector<char>> grid){

        int m = grid.size();
        int n = grid[0].size();
        int drows[] = {-1,0,1,0};
        int dcols[] = {0,1,0,-1};
        queue<pair<int,int>> q;

        q.push({i,j});
        vis[i][j] = '1';
        while(!q.empty()){
            auto it = q.front();
            q.pop();
            int row = it.first;
            int col = it.second;

            for(int k=0;k<4;k++){
                int nrow = row + drows[k];
                int ncol = col + dcols[k];
                if(nrow>=0 && nrow<m && ncol>=0 && ncol<n && vis[nrow][ncol] == 'O'){
                    q.push({nrow,ncol});
                    vis[nrow][ncol] = '1';
                }
            }
        }
    }
};
