class Solution {
public:
    void solve(vector<vector<char>>& board) {
        int m = board.size();
        int n = board[0].size();

        vector<vector<bool>> visited(m, vector<bool>(n, false));
        queue<pair<int, int>> q;

        for(int i = 0; i < m; i++)
        {
            for(int j = 0; j < n; j++)
            {
                if(i == 0 || i == m-1 || j == 0 || j == n-1)
                {
                    if(board[i][j] == 'O')
                    {
                        visited[i][j] = true;
                        q.push({i, j});
                    }
                }
            }
        }

        while(!q.empty())
        {
            int r = q.front().first;
            int c = q.front().second;

            q.pop();
            int drow[4] = {-1, 0, 1, 0};
            int dcol[4] = {0, 1, 0, -1};

            for(int i = 0; i < 4; i++)
            {
                int row = r + drow[i];
                int col = c + dcol[i];

                if(row >= 0 && row < m && col >= 0 && col < n && !visited[row][col] && board[row][col] == 'O')
                {
                    visited[row][col] = true;
                    q.push({row, col});
                }
            }
        }

        for(int i = 0; i < m; i++)
        {
            for(int j = 0; j < n; j++)
            {
                if(!visited[i][j] && board[i][j] == 'O')
                {
                    board[i][j] = 'X';
                }
            }
        }
    }
};
