    int DR[4]={1,0,-1,0};
    int DC[4]={0,-1,0,1};

    void dfs(int i,int j,vector<vector<int>> &vis,vector<vector<char>> &board){
      vis[i][j] = 1;
      int n = board.size();
      int m = board[0].size();

      //Check for top,right,bottom,left
      for(int k=0;k<4;k++){
          int ci = i + DR[k];
          int cj = j + DC[k];

          if(ci >= 0 && ci < n && cj >= 0 && cj < m && !vis[ci][cj] && board[ci][cj] == 'O' ){
              dfs(ci,cj,vis,board);
          }
      }

    }

    void solve(vector<vector<char>>& board) {
        int n = board.size();
        int m = board[0].size();

        vector<vector<int>> vis(n,vector<int> (m,0));

        //traverse first row and last row
        for(int j=0;j<m;j++){

            //First Row
            if(!vis[0][j] && board[0][j] == 'O'){
                dfs(0,j,vis,board);
            }

            //Last Row
            if(!vis[n-1][j] && board[n-1][j] == 'O'){
                dfs(n-1,j,vis,board);
            }
        }

        for(int i=0;i<n;i++){
            //First Column
            if(!vis[i][0] && board[i][0] == 'O'){
                dfs(i,0,vis,board);
            }

            if(!vis[i][m-1] && board[i][m-1] == 'O'){
                dfs(i,m-1,vis,board);
