    int dy[4]={0,0,-1,1};
    void DFS(std::vector<vector<char>>& board,int x,int y,int n,int m){
        if(x>=0 and y>=0 and x<n and y<m and board[x][y]=='O'){
                board[x][y]='#';
                for(int k=0;k<4;++k)
                    DFS(board,x+dx[k],y+dy[k],n,m);
        }  
    }
public:
    void solve(std::vector<vector<char>>& board) {
        int n=board.size(), m=board[0].size();

        if((n==1 and m==1) or !n or !m) 
