class Solution {
public:
    void solve(vector<vector<char>>& board) {
        int m = board.size();
        int n = board[0].size();
        queue<pair<int,int> >q;
        for(int i=0;i<m;i++) {
            for(int j=0;j<n;j++) {
                if(i==0 || j==0 || i==m-1 || j==n-1) {
                    if(board[i][j]=='O')
                        q.push({i,j});
                }
            }
        }
        int x1[4] = {1,-1,0,0};
        int y1[4] = {0,0,-1,1};
        while(!q.empty()) {
            pair<int,int> h = q.front();
            q.pop();
            int x = h.first;
            int y = h.second;
            board[x][y] = 'V';
            for(int k=0;k<4;k++) {
                int x2 = x + x1[k];
                int y2 = y + y1[k];
                if(x2>=0 && y2>=0 && x2<m && y2<n && board[x2][y2]=='O') {
                    q.push({x2,y2});
                    board[x2][y2] = 'V';              // visited
                }
            }
        }
        for(int i=0;i<m;i++) {
            for(int j=0;j<n;j++) {
                if(board[i][j]=='V') {       
                    board[i][j]='O';
                }
                else if(board[i][j]=='O') {
                    board[i][j]='X';
                }
            }
        }
    }
};
