//Main idea is that Convert non-flipable 'O' into another character by using DFS.So, We can easily convert flipable 'O' into 'X' by simple iteration.
class Solution {
public:
    void dfs(vector<vector<char>>& board,int i,int j,int m,int n)
    {
        if(i<0 || i>= m || j<0 || j>=n || board[i][j] != 'O') return;
        board[i][j] = '$';
        dfs(board,i-1,j,m,n);
        dfs(board,i+1,j,m,n);
        dfs(board,i,j-1,m,n);
        dfs(board,i,j+1,m,n);
    }

    void solve(vector<vector<char>>& board) 
    {
        int m = board.size(),n = board[0].size();
        //First row and last row
        for(int i =0;i<n;i++)
        {
            if(board[0][i]=='O') dfs(board,0,i,m,n);
            if(board[m-1][i]=='O')  dfs(board,m-1,i,m,n); 
        }

        //First column and last column
        for(int i =0;i<m;i++)
        {
            if(board[i][0]=='O') dfs(board,i,0,m,n);
            if(board[i][n-1]=='O') dfs(board,i,n-1,m,n);
        }

        for(int i =0;i<m;i++)
        {
            for(int j =0;j<n;j++)
            {
                if(board[i][j] == 'O') board[i][j] = 'X';
                else if(board[i][j]=='$') board[i][j] = 'O';
            }
        }
    }
};

