class Solution {
    void dfs(char[][] board, int r, int c) {
        int nr = board.length;
        int nc = board[0].length;

        //ensure the cell iswithin the boundary and its value is 'O'
        if (r<0 || r > nr-1 || c<0 || c > nc-1 || board[r][c] != 'O') return; 

        board[r][c] = '*'; //to mark the connected 'O's with a origin from the board boundary
        dfs(board, r-1, c);
        dfs(board, r, c-1);
        dfs(board, r+1, c);
        dfs(board, r, c+1);
    }

    public void solve(char[][] board) {
        int nr = board.length;
        int nc = board[0].length;

        //if 'O' is on the border
        for (int i=0; i<nr; i++) {
            if (board[i][0] == 'O') dfs(board, i,0);
            if (board[i][nc-1] == 'O') dfs(board, i,nc-1);
        }

        for (int i=0; i<nc; i++) {
            if (board[0][i] == 'O') dfs(board, 0, i);
            if (board[nr-1][i] == 'O') dfs(board, nr-1,i);
        }

        for(int r=0; r<nr; r++){
            for(int c=0; c<nc; c++) {
               if (board[r][c] == 'O') board[r][c] = 'X';
               if (board[r][c] == '*') board[r][c] = 'O';
            }
        }
    }
}
