class Solution 
{//T -> O(N^2)  S-> O(1)
    public void solve(char[][] board)
    {//We are treating each node as 
        int m= board.length;//number of row 
        int n= board[0].length;//number of column 

        //Axcept edge component or boundary component, all componennt are captured by X

        for(int i= 0; i < m; i++) 
        {
            for(int j= 0; j < n; j++) 
            {
                if(board[i][j] == 'O' && (i == 0 || j == 0 || i == m - 1 || j == n - 1)) //Finding the edge component nodes,//and filling that componenet nodes with -(dash), //indicating that the components are not covered by X on 4 sides//1 or two side is blocked  
                    Fill(board, i, j, m, n);
            }
        }

        //iterating over the matrix again to check that the edge related component must be replaced with O and rest of all the component must be replaced by X//since the component other than the edge must be surrounded by X on all 4 direction  
        for(int i = 0; i < m; i++)
        {
            for(int j = 0; j < n; j++) 
            {
                if(board[i][j] == '-') //Edge component remains same, according to  the question, //not able capture(support of wall)
                    board[i][j]= 'O';
                else //other than edge component are filled with X, //captured by X
                    board[i][j]= 'X';
            }
        }
        return;
    }
    public void Fill(char[][] board, int i, int j, int m, int n)//Filling the Edge Component nodes with Dash
    {//Preorder DFS
        if(i < 0 || j < 0 || i >= m  || j >= n || board[i][j] == '-' || board[i][j] == 'X') 
            return;//base case for returning back to the calling function when -> 1) when our index is out of bound 2) Already X condition 3) Already marked -(dash) condition(Visisted)

        //ROOT
        board[i][j]= '-';//filling the node of component of edge boundary with -(dash)

        //LEFT- RIGHT-TOP-BOTTOM// 4 direction coverage 
        Fill(board, i - 1, j, m, n);//Top
        Fill(board, i, j + 1, m, n);//Right
        Fill(board, i, j - 1, m, n);//Left 
        Fill(board, i + 1, j, m, n);//Bottom 

        return;
    }
}//Please do Upvote, it Helps a lot 
