class Solution {
    public void solve(char[][] board) {
        boolean[][]visited=new boolean[board.length][board[0].length];

        //Find boundaries from row=0 and row=board.length-1.
        for(int i=0;i<board[0].length;i++){
           //top row=0
           if(board[0][i]=='O' && visited[0][i]==false){
               dfs(board,0,i,visited);
           }
           //bottom row row=board.length-1
           if(board[board.length-1][i]=='O' && visited[board.length-1][i]==false){
               dfs(board,board.length-1,i,visited);
           }
        }

        //find boundaries in colums colum=0 and coloums=board[0].length-1
        for(int i=0;i<board.length;i++){
           //col=0  
            if(board[i][0]=='O' && visited[i][0]==false){
                dfs(board,i,0,visited);
            }
           //col= board[0].length-1
           if(board[i][board[0].length-1]=='O' && visited[i][board[0].length-1]==false){
               dfs(board,i,board[0].length-1,visited);
           }
        }
        // here we loop again through the matrix and any unvisited O visited[r][c]==fasle where board[r][c]=='O' we converted to X.
        for(int i=0;i<board.length;i++){
            for(int j=0;j<board[i].length;j++){
                if(board[i][j]=='O' && visited[i][j]==false)
                   board[i][j]='X';
            }
        }
    }

    void dfs(char[][] board,int r,int c,boolean[][]visited){
       if(r<0 || r>=board.length || c<0 || c>=board[0].length || board[r][c]=='X' || visited[r][c]==true)
         return;

         visited[r][c]=true;

         dfs(board,r,c+1,visited);
         dfs(board,r,c-1,visited);
         dfs(board,r+1,c,visited);
         dfs(board,r-1,c,visited);
    }


    }

